const BASEURL = "https://strangers-things.herokuapp.com/api/2207-FTB-ET-WEB-PT";

export const fetchPosts = async () => {
  try{
    const response = await fetch(`"https://strangers-things.herokuapp.com/api/2207-FTB-ET-WEB-PT/posts"`);
    console.log("fetchPosts is working!");
    const { data } = await response.json(); 
    //const result = await response.json(); //This could also work
    console.log("THIS IS DATA", data.posts);
    //console.log("THIS IS DATA", data.data)
    // //console.log("result", result.data.posts);
    // return result.data.posts;
    return data.posts;
  } catch(error){
    console.error("There was an error fetching posts", error)
  }
};

fetchPosts();


export const registerUser = async (username, password) => {
    try {
      const response = await fetch('https://strangers-things.herokuapp.com/api/2207-FTB-ET-WEB-PT/users/register', {
          method: "POST",
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            user: {
              username: username,
              password: password
              //username, 
              //password //alt code
            }
          })
        })
        return  response.json()
  } catch(error) {
      console.error(error)
  }
  }

  registerUser();
